    function add () {
        setInputValue(addName)
    }

    return (
        <div>
            <p>1</p>
            <input value={inputValue} onChange={(e) => setInputValue(e.target.value)}/>
            <button onClick={add}>Comments</button>
            <button onClick={() => {}}>Todos</button>
            <p>{addName}</p>
        </div>
    )
}
