import React, { useState } from "react";

export default function AppThree () {
    const [lightTheme, setLightTheme] = useState()
    const [darkTheme, setDarkTheme] = useState()


    function light () {
        setLightTheme((prev) => {
            const temaClaro = document.body.style.backgroundColor('#ffffff')
        })
    }

    function dark() {
        setLightTheme(darkTheme)
    }

    return (
        <div>
            <h1>Alternar modo claro e escuro</h1>
            <button onClick={light}>{}</button>
            <br />
            <button onClick={dark}>Escuro</button>
        </div>
    )
}
