import React, { useState } from "react";

export default function AppThree () {
    const [lights, setLights] = useState()


    function light () {
        setLights(document.body.style.backgroundColor('#fff'))
    }

    function dark() {

    }

    return (
        <div>
            <h1>Alternar modo claro e escuro</h1>
            <button onClick={light}>Claro</button>
            <br />
            <button onClick={dark}>Escuro</button>
        </div>
    )
}
