import React, { useState } from "react";

export default function AppFour () {
    const [count, setCount] = useState(0)
    const [message, setMessage] = useState('')

    function increment () {

            if (count < 10) {
                setCount(count + 1)

            if (count + 1 === 10)
                setMessage("Limite máximo atingido!")
                setTimeout( () => {
                    setMessage("")
                },1000)
            } 
        }

    function decrement () {
        if (count > 0) {
            setCount(count - 1)
            
        if(count - 1 === 0) {
            setMessage("Limite mínimo atingido")
            setTimeout( () => {
                setMessage("")
            }, 1000)
        }
        }
    }

    function onChangeInput () {

    }
        
    return (
        <div>
            <h1>Contador com limite</h1>
            <div>
                <h3>{count}</h3>
                {message && <p>{message}</p>}
            </div>
            <button onClick={increment} disabled={count === 10}>Incrementar</button>
            <button onClick={decrement} disabled={count === 0}>Decrementar</button>
            <div>
                <h2>Escolha seu limite máximo e mínimo</h2>
                <input onChange={onChangeInput}></input>
            </div>
        </div>
    )
}
