import React, { useState } from "react";

export default function AppFour() {
    const [count, setCount] = useState(0);
    const [message, setMessage] = useState("");
    const [highLimit, setHighLimit] = useState(0);
    const [lowLimit, setLowLimit] = useState(0);

    function increment() {
        setCount((prevCount) => {
            const newCount = prevCount + 1;

            if (newCount > highLimit) {
                setMessage("Limite máximo atingido!");
                setTimeout(() => setMessage(""), 2000);
                return prevCount; // Não altera o count
            }

            return newCount;
        });
    }

    function decrement() {
        setCount((prevCount) => {
            const newCount = prevCount - 1;

            if (newCount < lowLimit) {
                setMessage("Limite mínimo atingido!");
                setTimeout(() => setMessage(""), 2000);
                return prevCount; 
            }

            return newCount;
        });
    }

    function highLimits(event) {
        setHighLimit(Number(event.target.value));
    }

    function lowLimits(event) {
        setLowLimit(Number(event.target.value));
    }

    return (
        <div>
            <h1>Contador com limite</h1>
            <div>
                <h3>{count}</h3>
                {message && <p>{message}</p>}
            </div>

            <button onClick={increment} disabled={count >= highLimit}>
                Incrementar
            </button>
            <button onClick={decrement} disabled={count <= lowLimit}>
                Decrementar
            </button>

            <div>
                <h2>Escolha seu limite máximo e mínimo</h2>
                <label>Máximo:</label>
                <input type="number" onChange={highLimits} value={highLimit}/>
                <label>Mínimo:</label>
                <input type="number" onChange={lowLimits} value={lowLimit}/>
            </div>
        </div>
    );
}
