import React, { useState } from "react";

export default function App () {
    const [showText, setShowText] = useState(true)

    return (
        <div>
            <h1>Oi! {showText}</h1>
            <button onClick={() => setShowText (<h2>Texto</h2>)}>Mostrar Texto</button>
            <button>Remover texto</button>
           
        </div>
    )
}
